import React, {useState} from 'react'
import './style.css';
import FileBase from 'react-file-base64';
const Form = ()=>{
    const [postData, setPostData] = useState({ creatoremail: '', productname: '', price: '', selectedFile: '',password: ''});

    const handleSubmit = () =>{

    }

    return (
        <div>

            <form autoComplete="off" onSubmit={handleSubmit}>
                <h1>User Registration</h1>
                <label>FirstName :</label> <input type="text" width = "100%" value={postData.creatoremail} onChange={(e) => setPostData({ ...postData, creatoremail: e.target.value })} placeholder="Enter email" /><br />
                <label>LastName :</label> <input type="text" value={postData.productname} onChange={(e) => setPostData({ ...postData, productname: e.target.value })} placeholder="Enter productname" /><br />
                <label>Password :</label> <input type="password" value={postData.price} onChange={(e) => setPostData({ ...postData, price: e.target.value })} placeholder="Enter password" /><br />
                <div><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
                <label>Gender :</label><select onChange={this.genderhandler} defaultValue="Select Gender">
                    <option defaultValue>Select Gender</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                </select><br />
                <input type="submit" value="Submit" />
            </form>

        </div>
        
    );
}
export default Form;

